<?php/** * BelVG LLC. * * NOTICE OF LICENSE * * This source file is subject to the EULA * that is bundled with this package in the file LICENSE.txt. * It is also available through the world-wide-web at this URL: * http://store.belvg.com/BelVG-LICENSE-COMMUNITY.txt */********************************************** *        MAGENTO EDITION USAGE NOTICE        * **********************************************//* This package designed for Magento COMMUNITY edition * BelVG does not guarantee correct work of this extension * on any other Magento edition except Magento COMMUNITY edition. * BelVG does not provide extension support in case of * incorrect edition usage./********************************************** *        DISCLAIMER                          * **********************************************//* Do not edit or add to this file if you wish to upgrade Magento to newer * versions in the future. ********************************************** * @category   Belvg * @package    Belvg_Quickviewpro * @copyright  Copyright (c) 2010 - 2012 BelVG LLC. (http://www.belvg.com) * @license    http://store.belvg.com/BelVG-LICENSE-COMMUNITY.txt */class Belvg_Quickviewpro_Helper_Switcher extends Mage_Core_Helper_Abstract{    public function getExtensionName()
    {
        if ($this->isProductViewGalleryEnabled()) {            return 'Product View Gallery';        } elseif ($this->isProductZoomLightEnabled()) {            return 'Product Zoom Light';        }        return FALSE;    }
    /**     * The Product View Gallery is enabled/disabled     *     * @return boolean     */    public function isProductViewGalleryEnabled()    {        if (Mage::helper('core')->isModuleEnabled('Belvg_Productviewgallery') && Mage::getStoreConfig('productviewgallery/settings/enabled', Mage::app()->getStore())) {            return TRUE;        }        return FALSE;    }    /**     * The Product Zoom Light is enabled/disabled     *     * @return boolean     */    public function isProductZoomLightEnabled()    {        if (Mage::helper('core')->isModuleEnabled('Belvg_ProductZoomLight')            && (Mage::getStoreConfig('productzoomlight/cloudzoom/enabled', Mage::app()->getStore()) || Mage::getStoreConfig('productzoomlight/fancybox/enabled', Mage::app()->getStore())))        {            return TRUE;        }        return FALSE;    }    
    public function getSizes($type = '')    {        if ($this->isProductViewGalleryEnabled()) {            return array(                'mainWidth'   => Mage::helper('productviewgallery')->getMainWidth(),                'mainHeight'  => Mage::helper('productviewgallery')->getMainHeight(),                'thumbWidth'  => Mage::helper('productviewgallery')->getThumbWidth(),                'thumbHeight' => Mage::helper('productviewgallery')->getThumbHeight(),            );        } elseif ($this->isProductZoomLightEnabled()) {            return Mage::helper('productzoomlight')->getSizes($type);        } else {            return array(                'mainWidth'   => 900,                //'mainHeight'  => 1200,                'thumbWidth'  => 150,                'thumbHeight' => 150,            );        }    }}